        _cfg = cfg;
        _configuration = configuration;
    }

    [HttpGet]
    public async Task<IActionResult> Setup()
    {
        var user = await GetCurrentUserAsync();

        // Determine scope/mode
        var isAdmin = User.IsInRole("Admin");
        var showTotp = isAdmin;            // Admins: TOTP only
        var showSms = !isAdmin;            // Client/User: SMS OTP only

        string key = string.Empty;
        string otpauth = string.Empty;
        string qrDataUrl = string.Empty;
        if (showTotp)
        {
            key = await EnsureAuthenticatorKey(user);
            otpauth = BuildOtpAuthUri("AdevaPlus", user.Email ?? user.UserName ?? user.Id.ToString(), key);
            qrDataUrl = GenerateQrPngDataUrl(otpauth);
        }

        var (heading, subheading, tips) = BuildCopyForScope(isAdmin);

        // Prefer runtime-managed config; fallback to appsettings
        var bypass = await GetBypassAsync();

        // Persisted phone from last SendSms
        var lastSmsPhone = TempData.Peek("SmsPhone") as string;

        // Pre-fill phone: prefer last sent phone > confirmed user phone > dev default
        var prefillPhone = !string.IsNullOrWhiteSpace(lastSmsPhone)
            ? lastSmsPhone
            : (user.PhoneNumber ?? string.Empty);

        if (string.IsNullOrWhiteSpace(prefillPhone) && bypass)
        {
            prefillPhone = "+61400000000"; // Dev default
        }

        var vm = new TotpSetupVm
        {
            AuthenticatorKey = showTotp ? FormatKey(key) : string.Empty,
            OtpauthUri = showTotp ? otpauth : string.Empty,
            PhoneNumber = prefillPhone,
            TotpEnabled = user.TwoFactorEnabled,
            QrCodeDataUrl = showTotp ? qrDataUrl : string.Empty,
            ShowTotp = showTotp,
            ShowSms = showSms,
            Heading = heading,
            SubHeading = subheading,
            Tips = tips,
            DevMode = bypass,
            DevSmsCode = bypass ? "000000" : string.Empty
        };
        return View(vm);
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> SendSms([FromForm] string phoneNumber)
    {
        var user = await GetCurrentUserAsync();
        var isAdmin = User.IsInRole("Admin");
        if (isAdmin)
        {
            TempData["Error"] = "SMS verification is not required for administrator setup.";
            return RedirectToAction(nameof(Setup));
        }
        if (string.IsNullOrWhiteSpace(phoneNumber))
        {
            TempData["Error"] = "Phone number is required.";
            return RedirectToAction(nameof(Setup));
        }

        // Normalize and persist phone for the next step so token matches exactly
        var normalizedPhone = NormalizePhone(phoneNumber);
        TempData["SmsPhone"] = normalizedPhone;

        var bypass = await GetBypassAsync();
        if (!bypass)
        {
            var token = await _userManager.GenerateChangePhoneNumberTokenAsync(user, normalizedPhone);
            await _smsSender.SendSmsAsync(normalizedPhone, $"Adeva Plus verification code: {token}");
        }
        else
        {
            await _smsSender.SendSmsAsync(normalizedPhone, "Adeva Plus verification code: 000000");
        }

        TempData["Message"] = "Verification code sent via SMS.";
        return RedirectToAction(nameof(Setup));
    }

    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Complete([FromForm] TotpCompleteVm vm)
    {
        var user = await GetCurrentUserAsync();
        var isAdmin = User.IsInRole("Admin");
        var isClient = User.IsInRole("Client");
        var isUser = User.IsInRole("User");
        var showTotp = isAdmin;    // Admins: TOTP only
        var showSms = !isAdmin;    // Client/User: SMS OTP only

        var bypass = await GetBypassAsync();

        bool phoneVerified = false;
        bool totpVerified = false;

        // Conditional validation for SMS
        if (showSms)
        {
            // Prefer the last phone number we sent the SMS to
            var persistedPhone = TempData.Peek("SmsPhone") as string;
            var postedPhone = vm.PhoneNumber;
            var phoneToVerify = NormalizePhone(!string.IsNullOrWhiteSpace(persistedPhone) ? persistedPhone : postedPhone);

            if (string.IsNullOrWhiteSpace(phoneToVerify))
            {
                ModelState.AddModelError("PhoneNumber", "Phone number is required.");
            }
            else if (string.IsNullOrWhiteSpace(vm.SmsCode))
            {
                ModelState.AddModelError("SmsCode", "SMS verification code is required.");
            }
            else
            {
                if (bypass)
                {
                    // Bypass mode - accept any code (e.g., 000000) and auto-verify
                    user.PhoneNumber = phoneToVerify;
                    user.PhoneNumberConfirmed = true;
                    phoneVerified = true;
                    vm.PhoneNumber = phoneToVerify;
                }
                else
                {
                    // Atomically set + confirm phone using Identity helper
                    var result = await _userManager.ChangePhoneNumberAsync(user, phoneToVerify, vm.SmsCode!);
                    if (!result.Succeeded)
                    {
                        foreach (var err in result.Errors)
                        {
                            ModelState.AddModelError("SmsCode", err.Description);
                        }
                    }
                    else
                    {
                        phoneVerified = true;
                        vm.PhoneNumber = phoneToVerify;
                    }
                }
            }
        }

        // Conditional validation for TOTP
        if (showTotp)
        {
            if (string.IsNullOrWhiteSpace(vm.TotpCode))
            {
                ModelState.AddModelError("TotpCode", "Authenticator app code is required.");
            }
            else
            {
                if (bypass)
                {
                    // Bypass mode - accept any code and auto-verify
                    totpVerified = true;
                }
                else
                {
                    var verified = await _userManager.VerifyTwoFactorTokenAsync(user, TokenOptions.DefaultAuthenticatorProvider, vm.TotpCode!);
                    if (!verified)
                    {
                        ModelState.AddModelError("TotpCode", "Invalid authenticator code.");
                    }
                    else
                    {
                        totpVerified = true;
                    }
                }
            }
        }

        // Check for any validation errors
        if (!ModelState.IsValid)
        {
            // Store validation errors in TempData for display
            var validationErrors = ModelState
                .Where(x => x.Value?.Errors.Count > 0)
                .ToDictionary(
                    kvp => kvp.Key,
                    kvp => kvp.Value!.Errors.Select(e => e.ErrorMessage).ToArray()
                );

            TempData["ValidationErrors"] = JsonSerializer.Serialize(validationErrors);

            return RedirectToAction(nameof(Setup));
        }

        // Apply changes if verified (for bypass path, Identity helper already saved in normal path)
        if (phoneVerified && bypass)
        {
            await _userManager.UpdateAsync(user);
            TempData["SmsPhone"] = vm.PhoneNumber;
        }

        if (totpVerified)
        {
            await _userManager.SetTwoFactorEnabledAsync(user, true);
        }

        // Persist any remaining changes
        await _userManager.UpdateAsync(user);

        TempData["Message"] = isAdmin
            ? "Authenticator app set up successfully. TOTP is now required for admin sign-in."
            : "Phone verified successfully. We'll use SMS one-time codes to keep your account secure.";

        // Route to appropriate onboarding after security setup
        if (isAdmin) 
            return Redirect("/Admin");

        if (isClient)
        {
            // Client scope ? Organization onboarding
            var profile = await _db.UserProfiles.FirstOrDefaultAsync(p => p.UserId == user.Id);
            if (profile?.OrganizationId == null)
            {
                TempData["Message"] = "Phone verified! Let's set up your organization.";
                return Redirect("/Client/Onboarding");
            }
            TempData["Message"] = "Phone verified! Welcome back.";
            return Redirect("/Client");
        }

        if (isUser)
        {
            // User scope ? Debtor onboarding
            var profile = await _db.UserProfiles.FirstOrDefaultAsync(p => p.UserId == user.Id);
            if (profile?.DebtorId == null)
            {
                TempData["Message"] = "Phone verified! Let's complete your profile.";
                return Redirect("/User/Onboarding");
            }
            TempData["Message"] = "Phone verified! Welcome back.";
            return Redirect("/User");
        }

        // Fallback - if no role matched
        TempData["Error"] = $"Unable to determine user role. Admin: {isAdmin}, Client: {isClient}, User: {isUser}";
        return Redirect("/");
    }

    private static string NormalizePhone(string? phone)
    {
        if (string.IsNullOrWhiteSpace(phone)) return string.Empty;
        var p = phone.Trim();
        // Remove spaces, dashes, and parentheses
        p = new string(p.Where(c => char.IsDigit(c) || c == '+').ToArray());
        // Ensure it starts with '+' for E.164 if it looks like AU number
        if (!p.StartsWith("+") && p.StartsWith("0"))
        {
            // naive AU normalization 04xxxxxxxx -> +614xxxxxxxx
            if (p.StartsWith("04") && p.Length == 10)
            {
                p = "+61" + p.Substring(1);
            }
        }
        return p;
    }

    private async Task<ApplicationUser> GetCurrentUserAsync()
    {
        // Prefer lookup by ExternalAuthId from OIDC claims
        var externalId = User.FindFirstValue("oid") ?? User.FindFirstValue(ClaimTypes.NameIdentifier);
        if (!string.IsNullOrWhiteSpace(externalId))
        {
            var byExternal = await _db.Users.FirstOrDefaultAsync(u => u.ExternalAuthId == externalId);
            if (byExternal != null) return byExternal;
        }

        var name = User.Identity?.Name;
        if (!string.IsNullOrWhiteSpace(name))
        {
            var byName = await _userManager.FindByNameAsync(name);
            if (byName != null) return byName;
        }

        var byCookie = await _userManager.GetUserAsync(User);
        if (byCookie != null) return byCookie;

        throw new InvalidOperationException("User not found");
    }

    private async Task<string> EnsureAuthenticatorKey(ApplicationUser user)
    {
        var key = await _userManager.GetAuthenticatorKeyAsync(user);
        if (string.IsNullOrEmpty(key))
        {
            await _userManager.ResetAuthenticatorKeyAsync(user);
            key = await _userManager.GetAuthenticatorKeyAsync(user);
        }
        return key!;
    }

    private static string BuildOtpAuthUri(string issuer, string account, string secret)
    {
        return $"otpauth://totp/{Uri.EscapeDataString(issuer)}:{Uri.EscapeDataString(account)}?secret={secret}&issuer={Uri.EscapeDataString(issuer)}&digits=6";
    }

    private static string FormatKey(string key)
    {
        var groups = new List<string>();
        for (int i = 0; i < key.Length; i += 4)
        {
            groups.Add(key.Substring(i, Math.Min(4, key.Length - i)));
        }
        return string.Join(" ", groups);
    }

    private static string GenerateQrPngDataUrl(string text)
    {
        using var generator = new QRCodeGenerator();
        using var data = generator.CreateQrCode(text, QRCodeGenerator.ECCLevel.Q);
        var png = new PngByteQRCode(data);
        var bytes = png.GetGraphic(20);
        return "data:image/png;base64," + Convert.ToBase64String(bytes);
    }

    private static (string heading, string subheading, List<string> tips) BuildCopyForScope(bool isAdmin)
    {
        if (isAdmin)
        {
            return (
                heading: "Secure your admin account with an Authenticator App",
                subheading: "Admins must use Timebased OneTime Passwords (TOTP). Scan the QR code and enter the 6digit code from your app to finish.",
                tips: new List<string>
                {
                    "Use any authenticator app (Microsoft, Google, Authy).",
                    "Back up your recovery codes in your password manager.",
                    "TOTP will be required on every admin signin."
                }
            );
        }
        return (
            heading: "Verify your phone number",
            subheading: "We send a onetime SMS code to confirm it's you. This keeps your account secure without needing an authenticator app.",
            tips: new List<string>
            {
                "Enter a mobile number where you can receive SMS messages.",
                "If you don't receive a code, check the number and try again.",
                "You can update your phone later from your profile."
            }
        );
    }

    private async Task<bool> GetBypassAsync()
    {
        var v = await _cfg.GetAsync<string>("Security:BypassOtpVerification");
        if (!string.IsNullOrWhiteSpace(v) && bool.TryParse(v, out var parsed))
        {
            return parsed;
        }
        return _configuration.GetValue<bool>("Security:BypassOtpVerification");
    }
}

public class TotpSetupVm
{
    public string AuthenticatorKey { get; set; } = string.Empty;
    public string OtpauthUri { get; set; } = string.Empty;
    public string PhoneNumber { get; set; } = string.Empty;
    public bool TotpEnabled { get; set; }
    public string QrCodeDataUrl { get; set; } = string.Empty;

    // UI mode
    public bool ShowTotp { get; set; }
    public bool ShowSms { get; set; }

    // Copy
    public string Heading { get; set; } = string.Empty;
    public string SubHeading { get; set; } = string.Empty;
    public List<string> Tips { get; set; } = new();

    // Dev mode
    public bool DevMode { get; set; }
    public string DevSmsCode { get; set; } = string.Empty;
}

public class TotpCompleteVm
{
    // All optional; validated conditionally based on role
    public string? PhoneNumber { get; set; }
    public string? SmsCode { get; set; }
    public string? TotpCode { get; set; }
}

using DebtManager.Web;
using DebtManager.Web.Services;
using Microsoft.AspNetCore.Mvc;
using DebtManager.Infrastructure;
using DebtManager.Infrastructure.Persistence;
using DebtManager.Application;
using Hangfire;
using Hangfire.SqlServer;
using Microsoft.EntityFrameworkCore;
using Serilog;
using Microsoft.Identity.Web;
using Microsoft.AspNetCore.Authentication.OpenIdConnect;
using Microsoft.AspNetCore.Authorization;
using DebtManager.Infrastructure.Identity;
using Microsoft.AspNetCore.Identity;
using DebtManager.Web.Auth;
using Microsoft.AspNetCore.Authentication;
using Microsoft.AspNetCore.Authentication.Cookies;
using DebtManager.Web.Middleware;
using DebtManager.Web.Jobs;

var builder = WebApplication.CreateBuilder(args);

// Serilog basic setup (keep config-driven to avoid eager provider initialization)
// Tip: Use appsettings to reduce noisy providers at boot (e.g., Microsoft to Warning)
builder.Host.UseSerilog((ctx, lc) => lc.ReadFrom.Configuration(ctx.Configuration));

// MVC + conditionally add runtime compilation only for local dev
var mvcBuilder = builder.Services.AddControllersWithViews();
if (builder.Environment.IsDevelopment())
{
    mvcBuilder.AddRazorRuntimeCompilation();
}

builder.Services.AddHttpContextAccessor();

// Maintenance state
builder.Services.AddSingleton<DebtManager.Web.Services.IMaintenanceState, DebtManager.Web.Services.MaintenanceState>();

// Health checks
builder.Services.AddHealthChecks();

// EF Core
var cs = builder.Configuration.GetConnectionString("Default") ?? "Server=(localdb)\\MSSQLLocalDB;Database=DebtManager;Trusted_Connection=True;";
builder.Services.AddDbContext<AppDbContext>(opts => opts.UseSqlServer(cs));

// Identity Core using EF stores
builder.Services
    .AddIdentityCore<ApplicationUser>(options =>
    {
        options.User.RequireUniqueEmail = false; // External identity provides uniqueness by oid
        options.SignIn.RequireConfirmedAccount = false;
        options.Tokens.AuthenticatorTokenProvider = TokenOptions.DefaultAuthenticatorProvider;
    })
    .AddRoles<ApplicationRole>()
    .AddEntityFrameworkStores<AppDbContext>()
    .AddSignInManager()
    .AddDefaultTokenProviders();

// Configure authentication: Cookies as default auth, OIDC as challenge.
builder.Services
    .AddAuthentication(options =>
    {
        options.DefaultScheme = CookieAuthenticationDefaults.AuthenticationScheme;
        options.DefaultChallengeScheme = OpenIdConnectDefaults.AuthenticationScheme;
    })
    .AddMicrosoftIdentityWebApp(options =>
    {
        var b2c = builder.Configuration.GetSection("AzureAdB2C");
        options.ClientId = b2c["ClientId"]!;
        options.Instance = b2c["Instance"] ?? "https://jsraauth.b2clogin.com/";
        options.Domain = b2c["Domain"] ?? "jsraauth.onmicrosoft.com";
        options.Authority = b2c["Authority"]!;
        options.ClientSecret = b2c["ClientSecret"]; // Added client secret
        options.CallbackPath = b2c["CallbackPath"] ?? "/signin-oidc";
        options.SignedOutCallbackPath = b2c["SignedOutCallbackPath"] ?? "/signout-callback-oidc";
        options.TokenValidationParameters.NameClaimType = "name";
        options.Events = new OpenIdConnectEvents
        {
            OnTokenValidated = async ctx => await TokenValidatedHandler.OnTokenValidated(ctx)
        };
    })
    .EnableTokenAcquisitionToCallDownstreamApi()
    .AddInMemoryTokenCaches();

// Configure the cookie handler options (scheme added by MicrosoftIdentityWeb)
builder.Services.Configure<CookieAuthenticationOptions>(CookieAuthenticationDefaults.AuthenticationScheme, options =>
{
    options.LoginPath = "/Dev/Login";
    options.AccessDeniedPath = "/Dev/Login";
    options.SlidingExpiration = true;
});

// Claims transformation to map scopes -> roles
builder.Services.AddScoped<IClaimsTransformation, B2CRoleClaimsTransformation>();

// Hangfire (SQL Server for persistence and audit)
var hangfireCs = builder.Configuration.GetConnectionString("Hangfire") ?? cs;
builder.Services.AddHangfire(cfg => cfg
    .UseSimpleAssemblyNameTypeSerializer()
    // Avoid forcing Newtonsoft/advanced serializer settings to keep startup lean
    .UseSqlServerStorage(hangfireCs));
// Remove AddHangfireServer to avoid crashing the host when storage is unavailable
// builder.Services.AddHangfireServer();

// Branding resolver
builder.Services.AddScoped<BrandingResolverMiddleware>();
builder.Services.AddScoped<IAdminService, AdminService>();

// Messaging services (generic + onboarding orchestration)
builder.Services.AddScoped<IMessageQueueService, MessageQueueService>();
builder.Services.AddScoped<IOnboardingNotificationService, OnboardingNotificationService>();
builder.Services.AddScoped<MessageDispatchJob>();
builder.Services.AddScoped<IBusinessLookupService>(x =>
{
    var logger = x.GetRequiredService<ILogger<AbrBusinessLookupService>>();
    var key = builder.Configuration["AbrApi:ApiKey"];
    return new AbrBusinessLookupService(builder.Configuration["AbrApi:ApiKey"] ?? string.Empty , logger);
});

builder.Services.AddInfrastructure(builder.Configuration);
builder.Services.AddApplication();

builder.Services.AddAuthorization(options =>
{
    var scopes = builder.Configuration.GetSection("AzureB2CScopes");
    var userScope = scopes["User"] ?? string.Empty;
    var clientScope = scopes["Client"] ?? string.Empty;
    var adminScope = scopes["Admin"] ?? string.Empty;
    options.AddPolicy("RequireUserScope", policy =>
        policy.RequireAssertion(ctx => ctx.User.HasClaim("scp", userScope)));
    options.AddPolicy("RequireClientScope", policy =>
        policy.RequireAssertion(ctx => ctx.User.HasClaim("scp", clientScope)));
    options.AddPolicy("RequireAdminScope", policy =>
        policy.RequireAssertion(ctx => ctx.User.HasClaim("scp", adminScope)));
});

var app = builder.Build();

// Initialize database (lean): migrate; optional seed is handled inside initializer via config flags
var maintenance = app.Services.GetRequiredService<IMaintenanceState>();
try
{
    // await DebtManager.Web.Data.DbInitializer.InitializeAsync(app.Services, app.Environment);
}
catch (Exception ex)
{
    // Enter maintenance mode and log the startup exception. App will stay up and serve 503.
    maintenance.Enable(ex);
    Log.ForContext("Startup", true).Error(ex, "Startup initialization failed; entering maintenance mode.");
}

app.UseSerilogRequestLogging();

// Maintenance mode should be evaluated as early as possible
app.UseMaintenanceMode();

if (!app.Environment.IsDevelopment())
{
    app.UseExceptionHandler("/Home/Error");
    app.UseHsts();
}

app.UseHttpsRedirection();
app.UseStaticFiles();

app.UseRouting();

app.UseAuthentication();

// Dev-only auth redirect middleware
if (app.Environment.IsDevelopment() && app.Configuration.GetValue<bool>("DevAuth:EnableFakeSignin"))
{
    app.UseMiddleware<DevAuthRedirectMiddleware>();
}

app.UseAuthorization();

// Enforce security onboarding (TOTP, phone, client org)
app.UseMiddleware<SecurityEnforcementMiddleware>();

// Admin bootstrap nudge (after auth)
app.UseMiddleware<BootstrapNudgeMiddleware>();

app.UseMiddleware<BrandingResolverMiddleware>();

app.MapHealthChecks("/health/live");
app.MapHealthChecks("/health/ready");

// Hangfire Dashboard - Secured with Admin scope
app.MapHangfireDashboard("/hangfire", new Hangfire.DashboardOptions
{
    Authorization = new[] { new HangfireAuthorizationFilter() }
});

// Areas routing
app.MapControllerRoute(
    name: "areas",
    pattern: "{area:exists}/{controller=Home}/{action=Index}/{id?}");

// Default route
app.MapControllerRoute(
    name: "default",
    pattern: "{controller=Home}/{action=Index}/{id?}");

// Simple API for recipient search (admin)
app.MapGet("/api/admin/usersearch", async ([FromQuery] string q, AppDbContext db) =>
{
    if (string.IsNullOrWhiteSpace(q) || q.Length < 2) return Results.Json(Array.Empty<object>());
    q = q.ToLowerInvariant();
    var results = await db.Users
        .Where(u => (u.Email ?? "").ToLower().Contains(q) || (u.UserName ?? "").ToLower().Contains(q))
        .OrderBy(u => u.Email)
        .Take(10)
        .Select(u => new { id = u.Id, name = u.UserName ?? u.Email ?? "Unknown", email = u.Email ?? string.Empty })
        .ToListAsync();
    return Results.Json(results);
}).RequireAuthorization(new AuthorizeAttribute { Policy = "RequireAdminScope" });

// Configure recurring jobs only if not in maintenance and server is enabled
if (!maintenance.IsMaintenance)
{
    var serverEnabled = app.Configuration.GetValue<bool>("Hangfire:ServerEnabled");
    if (serverEnabled)
    {
        BackgroundJobServer? server = null;
        try
        {
            server = new BackgroundJobServer();
            // Configure recurring jobs
            DebtManager.Web.Jobs.NightlyJobs.ConfigureRecurringJobs();
            RecurringJob.AddOrUpdate("dispatch-queued-messages-di", () => app.Services.GetRequiredService<MessageDispatchJob>().RunAsync(CancellationToken.None), Cron.Minutely);
            // Dispose server gracefully on shutdown
            var lifetime = app.Services.GetRequiredService<IHostApplicationLifetime>();
            lifetime.ApplicationStopping.Register(() => server.Dispose());
        }
        catch (Exception ex)
        {
            maintenance.Enable(ex);
            Log.Error(ex, "Failed to start Hangfire server; entering maintenance mode.");
        }
    }
}

app.Run();

